    def ShowData(self):
        conn =cn.connect(user='root',password='',host='localhost',database='toyo')
        result=conn.cursor()
        result.execute('select * from tb_product limit 10')
        mydata=result.fetchall()
        #self.tableWidget.setRowCount(10)
        self.tbShowData.setRowCount(10)
        tablerow=0
        for row in mydata:
           
            self.tbShowData.setItem(tablerow,0,QtWidgets.QTableWidgetItem(row[0]))
            self.tbShowData.setItem(tablerow,1,QtWidgets.QTableWidgetItem(row[1]))
            self.tbShowData.setItem(tablerow,2,QtWidgets.QTableWidgetItem(str(row[2])))
            self.tbShowData.setItem(tablerow,3,QtWidgets.QTableWidgetItem(str(row[3])))
            tablerow+=1
            conn.close()
    
    def SearchProduct(self):
        conn =cn.connect(user='root',password='',host='localhost',database='toyo')
        cs=conn.cursor()
