    def AddData(self):
        conn =cn.connect(user='root',password='',host='localhost',database='toyo')
        cs=conn.cursor()
        pid=self.txtProductID.text()
        pname=self.txtProductName.text()
        pprices=self.txtPrices.text()
        ptotal=self.txtTotal.text()
        insertpro="INSERT INTO tb_product(productID,ProductName,price,total)values(%s,%s,%s,%s)"
        val=(pid,pname,pprices,ptotal)
        cs.execute(insertpro,val)
        conn.commit()
    
        self.txtProductID.clear()
        self.txtProductName.clear()
        self.txtPrices.clear()
        self.txtTotal.clear()
        conn.close()
